        client.tokenTest(request, {"token": "qwertyuiop"}, (err, response) => {
            if (err) {
                console.log(`Unexpected error for gRPC: code = ${err.code}` +
                    `, message = "${err.message}"`);
            } else {
                console.log(response.getMessage());
            }
        });
        this.state = {};
    }

    render() {
        return (
            <div>
